import * as types from "./actionType";

const intialState = {
   user: [],
   users: {},
   loading: true,
 };

 const usersReducers = (state = intialState, action) => {
   switch (action.type) {
            case types.GET_USERS:
       return {
         ...state,
         users: action.payload,
         loading: false,
       };



   default:
       return state;
   }
 };
 export default usersReducers;
